import { useState, useEffect } from 'react';
import '../styles/PartsGrid.css';

const allParts = [
  { id: 1, name: 'Brake Pads', price: 29.99, brand: 'Bosch', compatibility: 'Toyota', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR-81uDWuVZZFnfv_PjVt0hZrA-HxmiltVl3A&s' },
  { id: 2, name: 'Oil Filter', price: 12.99, brand: 'Denso', compatibility: 'Honda', image: 'data:image/jpeg;base64,' },
  { id: 3, name: 'Air Filter', price: 15.99, brand: 'NGK', compatibility: 'Ford', image: 'data:image/jpeg;base64,' },
  { id: 4, name: 'Spark Plugs', price: 8.99, brand: 'Bosch', compatibility: 'Toyota', image: 'data:image/jpeg;base64,' },
  { id: 5, name: 'Car Battery', price: 89.99, brand: 'Denso', compatibility: 'Honda', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS4HcPWRgfJLCQLAajzxuC1izAspTDK3RbQ_A&s' },
  { id: 6, name: 'Radiator', price: 120.00, brand: 'NGK', compatibility: 'Ford', image: 'data:image/jpeg;base64,' },
  { id: 7, name: 'Alternator', price: 150.00, brand: 'Bosch', compatibility: 'Toyota', image: 'https://via.placeholder.com/200' },
  { id: 8, name: 'Fuel Pump', price: 45.00, brand: 'Denso', compatibility: 'Honda', image: 'https://via.placeholder.com/200' },
];

function PartsGrid({ filters, addToCart }) {
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 6;

  const filteredParts = allParts.filter((part) => {
    const matchesSearch = part.name.toLowerCase().includes(filters.search.toLowerCase());
    const matchesPrice =
      part.price >= filters.priceRange[0] && part.price <= filters.priceRange[1];
    const matchesBrand = filters.brand ? part.brand === filters.brand : true;
    const matchesCompatibility = filters.compatibility
      ? part.compatibility === filters.compatibility
      : true;
    return matchesSearch && matchesPrice && matchesBrand && matchesCompatibility;
  });

  const totalPages = Math.ceil(filteredParts.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const currentParts = filteredParts.slice(startIndex, startIndex + itemsPerPage);

  useEffect(() => {
    setCurrentPage(1); // Reset to page 1 when filters change
  }, [filters]);

  return (
    <section className="parts-grid">
      <div className="grid">
        {currentParts.length > 0 ? (
          currentParts.map((part) => (
            <div key={part.id} className="part-card">
              <img src={part.image} alt={part.name} />
              <h3>{part.name}</h3>
              <p>Brand: {part.brand}</p>
              <p>Compatibility: {part.compatibility}</p>
              <p className="price">${part.price}</p>
              <button onClick={() => addToCart(part)}>Add to Cart</button>
            </div>
          ))
        ) : (
          <p>No parts found matching your criteria.</p>
        )}
      </div>
      {totalPages > 1 && (
        <div className="pagination">
          {[...Array(totalPages)].map((_, index) => (
            <button
              key={index}
              onClick={() => setCurrentPage(index + 1)}
              className={currentPage === index + 1 ? 'active' : ''}
            >
              {index + 1}
            </button>
          ))}
        </div>
      )}
    </section>
  );
}

export default PartsGrid;